import React, { Component } from "react";
import './Contact.css'

export default class ContactPage extends Component {
  // Implement a form validation for the contact form
  constructor(props) {
    super(props);
    this.validate = this.validate.bind(this);
  }
  validate(event) {

    event.preventDefault();
    var fName = document.getElementById("fname").value;
    var lName = document.getElementById("lname").value;
    var email = document.getElementById("email").value;
    var comments = document.getElementById("comments").value;
    var error_message = document.getElementById("error_message");
    error_message.style.padding = "10px";

    var text;
    if (fName.length < 2) {
      text = "Please Enter First Name";
      error_message.innerHTML = text;
      return false;
    }
    if (lName.length < 1) {
      text = "Please Enter Last Name";
      error_message.innerHTML = text;
      return false;
    }
    if (email.indexOf("@") == -1 || email.length < 10) {
      text = "Please Enter valid Email";
      error_message.innerHTML = text;
      return false;
    }
    alert("Form Submitted Successfully!");
    this.form.submit();
    return true;
  }


  // Using the method getElementById() returns an element object representing the ID property.
  // You can use querySelector() to find the element using any selector. No ID.
  // The indexOf() method returns the position of the first occurrence of a specified value in a string.

  render() {
    return (
        <div>
        <main class="contact__container" />
        <form ref={(form => this.form = form)} onSubmit={this.validate} />
        <label for="fname">First Name</label>
        <input class="form__box" type="text" id="fname" name="firstname" placeholder="Your name.." />

        <label for="lname">Last Name</label>
        <input class="form__box" type="text" id="lname" name="lastname" placeholder="Your last name.." />

        <label for="email">Email Address</label>
        <input class="form__box" type="text" id="email" name="email" placeholder="Your Email Address.." />

        <label for="comments">Comments</label>
        <textarea class="form__box" id="comments" name="comments"
            placeholder="Please write here for any question.."></textarea>

        <input class="submit__button" type="submit" value="Submit" />

        <div id="error_message"></div>
    </div>
    );
  };

};
