import React from 'react';
import Product from '../Components/ProductItems';
import '../SCSS/ProductBody.scss';
import Products from '../Components/Products.json'

export default class ProductsBody extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        filteredProducts: []
      }
      this.filterPrice = this.filterPrice.bind(this);
    }
    componentDidMount() {
      this.filterPrice();
    }
    filterPrice(high, low) {
      this.setState({
        filteredProducts: ProductsInfo.filter((product) => {
          // is product less than high and greater than low.
          if (product.price > high) {
            return false;
          } else if (product.price < low) {
            return false;
          }
          return true;
        })
      })
    };
    render() {
      return (
        <div className="grid-container">
          <PriceFilter onFilter={this.filterPrice} />
          {this.state.filteredProducts.map((productInfo, index) => {
            return <Product product={productInfo} />
          })}
        </div>
      );
    }
  };
